// == IMPORT NPM
import axios from "axios";

// == IMPORT ASSETS
import {
  CREATE_USER,
  GET_USERS_LIST, 
  setUsersList,
} from '../actions/user';

const usersApi = (store) => (next) => (action) =>{

  const { users : {
    email,
    name,
    password
  }} = store.getState();

  const config = {
    headers: {
      "Content-type": "application/json"
    }
  };


  switch (action.type) {

    case CREATE_USER:
      axios.post('http://denisjulien-server.eddi.cloud/projet-9-sound-clock-back/public/api/user/new' , {
        email,
        name,
        password  
      }, config
        ).then((response) => {
          console.log('Le data a était envoyé, all is good.',response);
        })
        .catch((error) => {
          console.log('error nul', error);
        });
      break;

    case GET_USERS_LIST: 
      axios.get(`${baseURL}/api/users/top10/listened`)
      .then((response) => {
          store.dispatch(setUsersList(response.data));
        })
      .catch((error) => {
          // eslint-disable-next-line no-console
          console.log('error', error);
        });
      break;
    default:
    next(action);   
  }
};
export default usersApi;
