// == IMPORT NPM
import React from "react";
import {Header, Modal, Button, Icon, Form, Input, Message } from "semantic-ui-react";
import './styles.scss'

const ModalCreate = ({
  name,
  email,
  password,
  changeField,
  handleCreate,
  errorCode
  //changeModalState,
}) => { 

  const handleChange = (evt) => {
    changeField(evt.target.value, evt.target.name);
  };

  const handleSubmit = (evt) => {
    evt.preventDefault();
    handleCreate();
  }; 

 /*  const handleModalState = (evt) => {
    dispatch(changeModalState(evt.target.name));
  } */

  //const createButton = useSelector((state) => state.users.createButton);
  //onClick={handleModalState}
  const [open, setOpen] = React.useState(false)
  return (
  <>


    <Modal
    centered={false}
    //onClose={{handleModalState}}
    onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      trigger={<Button   name="createButton" color="teal" size="big" >
      <Icon name="add user" />
      Create Account
    </Button>}
      >
      <Header size='large' color="teal">Creation de votre compte</Header>
      <Modal.Content>

        <Form className="modal__creat__form" onSubmit={handleSubmit}>
        <Form.Field>
          <Input
            className="navbar__login-btns"
            label={{ icon: 'asterisk', color: 'teal'  }}
            labelPosition='left corner'
            icon={<Icon name='user circle' color='teal' />}
            iconPosition='left'
            placeholder='pseudo'
            size='huge'
            type='text'
            required
            fluid 
            onChange={handleChange}
            name='name'
            value={name}
          />
          </Form.Field>
          <Form.Field>
          <Input
            className="navbar__login-btns"
            label={{ icon: 'asterisk', color: 'teal'  }}
            labelPosition='left corner'
            icon={<Icon name='mail' color='teal' />}
            iconPosition='left'
            placeholder='e-mail'
            size='huge'
            type='email'
            required
            fluid 
            onChange={handleChange}
            name='email'
            value={email}
          />
          </Form.Field>
          <Form.Field>
          <Input
            className="navbar__login-btns"
            label={{ icon: 'asterisk', color: 'teal'  }}
            labelPosition='left corner'
            icon={<Icon name='lock' color='teal' />}
            iconPosition='left'
            placeholder='mot de passe'
            size='huge'
            type='password'
            required
            fluid 
            onChange={handleChange}
            name='email'
            value={email}
          />
          </Form.Field>
        {/*  <Form.Field >
            <Input icon='lock' iconPosition='left' placeholder='Confirm Password' type="password" onChange={handleChange} />
          </Form.Field> */}

          <Button size='huge' fluid color='teal' type='submit'>Créer</Button>

          {errorCode === 422 && 
            <Message className="error__mail">
              <Message.Header className="error__mail__content">L'adresse E-mail existe déjà !</Message.Header>
            </Message>
          }
        </Form>

      </Modal.Content>
    </Modal>
  </>

  );

};

export default ModalCreate;
