import React, { useState } from 'react';
import {NavLink} from 'react-router-dom'
import { List, Button, Image} from 'semantic-ui-react'
import { useSelector, useDispatch } from 'react-redux';

import { showMusicLanding, hideMusicLanding } from '../../actions/musics';
import { showPlaylistLanding, hidePlaylistLanding } from '../../actions/playlist';
import { showUserLanding, hideUserLanding } from '../../actions/user';

import './styles.scss';


const LandingPage = () => {
    const dispatch = useDispatch();

    const musics = useSelector((state) => state.musics.list);
    let musicsState = useSelector((state) => state.musics.btnState);
    //console.log('Object sur view column', musics);

    const users = useSelector((state) => state.users.list);
    let usersState = useSelector((state) => state.users.btnState);
    //console.log('Object sur view column', users);  

    const playlists = useSelector((state) => state.playlists.list);
    let playlistsState = useSelector((state) => state.playlists.btnState);
    //console.log('Object sur view column', playlists);


    const handleMusic = () => {
        console.log('je clique sur le bouton music depuis landing ')
        dispatch(showMusicLanding());
        dispatch(hidePlaylistLanding())
        dispatch(hideUserLanding())
        /* musicsState = true;
        usersState = false;
        playlistsState = true; */


    }
    const handleArtiste = () => {
        console.log('je clique sur le bouton artist ')
        dispatch(showUserLanding())
        dispatch(hideMusicLanding())
        dispatch(hidePlaylistLanding())

        /* musicsState = false;
        usersState = true;
        playlistsState = false; */

    }
    const handlePLaylist = () => {
        console.log('je clique sur le bouton playlist ')
        dispatch(showPlaylistLanding())
        dispatch(hideUserLanding())
        dispatch(hideMusicLanding())
        /* musicsState = false;
        usersState = false;
        playlistsState = true; */
    }


    return (
        <>
        <Button.Group className='button__list'>
            <Button onClick={handleMusic}>
               Musics
            </Button>
            <Button onClick={handleArtiste}>
                Artistes
            </Button>
            <Button onClick={handlePLaylist}>
                Playlist
            </Button>
        </Button.Group>

        <div className='container-main'>

        <div className='container-list' >
            { musicsState && 
            <>
                <List divided relaxed className='list-music'>
                {musics.slice(0,10).map((music) => (
                    <List.Item>
                    <Image avatar src={music.picture} />
                            <List.Content>
                                <List.Header>{music.title}</List.Header>
                                <List.Description>{music.description}</List.Description>
                            </List.Content>
                    </List.Item>
                ))}
                </List>
            </>
            }
            { usersState && 
            <>
                <List divided relaxed className='list-user'>
                {users.slice(0,10).map((user) => (
                    <List.Item>
                    <Image avatar src={user.picture} />
                            <List.Content>
                                <List.Header>{user.name}</List.Header>
                                <List.Description>{user.description}</List.Description>
                            </List.Content>
                    </List.Item>
                ))}
                </List>
            </>
            }
            { playlistsState &&
            <>
                <List divided relaxed className='list-playlist'>
                {playlists.slice(0,10).map((playlist) => (
                    <List.Item>
                    <Image avatar src={playlist.picture} />
                            <List.Content>
                                <List.Header>{playlist.title}</List.Header> 
                                <List.Description>{playlist.description}</List.Description> 
                            </List.Content>
                    </List.Item>
                ))}
                </List>   
            </>
}
    </div>

    <div className='container-image'><Image className="img" size='big' src='https://m.media-amazon.com/images/I/81B1nzZcddL._SL1444_.jpg' fluid /></div> 

    </div>
  </>
    );

};

export default LandingPage;
