// IMPORT NPM
import React from 'react';
import { useParams, useLocation } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { useEffect } from 'react';


// IMPORT ASSETS
import './styles.scss';
import { getPlaylistByID } from '../../actions/playlist';

const Playlist = () => {

  const dispatch = useDispatch();
  const location = useLocation();
  const selectSate = useSelector((state) => state.playlists.selectSate);

  //* useParams used to pickup the slug is the user is not logged
  const { playlist } = useParams();
  useEffect(
    () => {
      console.log('test useEffect playlist wiew');
      dispatch(getPlaylistByID(playlist));
      window.scrollTo(0, 0);
    }, [dispatch, location]
  );

  const details = useSelector((state) => state.playlists.detaims);
  console.log(details);

  return(
    <>
    {selectSate && (
      <section className='playlist__container'>

        <div className='playlist__avatar--container'>
          <div className="playlist__avatar" style={{"backgroundImage": `url(${details.picture})` }}></div>
          <h1 className='playlist__h1'>Playlist: <span className='playlist__h1--span'>{ playlist }</span></h1>
        </div>

      </section>
    )}
    </>
  );
};

export default Playlist;
