// == IMPORT NPM
import React from "react";
import { useNavigate } from "react-router-dom";
import { Modal, Button, Icon, Form, Message,Header } from "semantic-ui-react";
import PropTypes from 'prop-types';
import { useSelector } from "react-redux";

const ModalConnexion = ({  
  username,
  password,
  changeField,
  handleLogin,
}) => { 

  const navigate = useNavigate();

  const handleChange = (evt) => {
    changeField(evt.target.value, evt.target.name);
  };

  const userSlug = useSelector((state) => state.users.user.slug);

  const handleSubmit = (evt) => {
    evt.preventDefault();
    handleLogin();
    navigate(`/user/${user.slug}`);
    console.log('je suis le slug sur la page user : ' , userSlug);
  };

  /* const handleModalState = (evt) => {
    dispatch(changeModalState(evt.target.name));
  } */

  //const loginButton = useSelector((state) => state.users.loginButton);
  //onClick={handleModalState}
  const [open, setOpen] = React.useState(false)
  return(
    <>   
      <Modal
      centered={false}
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      trigger={ <Button  name="loginButton" color="teal" size="big">
        <Icon name="sign-in" />
        Sign in
      </Button>}
      >
      <Header color="teal">Connectez vous à votre compte</Header>
      <Modal.Content>
        <Modal.Description>
          <Form  onSubmit={handleSubmit}>
            <Form.Field>
            <Form.Input 
              fluid icon='user' 
              iconPosition='left' 
              placeholder='E-mail' 
              onChange={handleChange}
              name='username'
              value={username}
            />
            <Form.Input
              fluid
              icon='lock'
              iconPosition='left'
              placeholder='Password'
              type='password'
              onChange={handleChange}
              name='password'
              value={password}
            />
            </Form.Field>
            {// <Form.Field>
              //<Checkbox label='I agree to the Terms and Conditions' />
            // </Form.Field>
            }
            <Button   color='teal' fluid size='large'>
              Login
            </Button>
          </Form>
        </Modal.Description>
      </Modal.Content>
      <Message>
          Nouveau ici ? <a href='/#'>Creer un compte</a>
        </Message>
      {
      //<Modal.Actions>
      //  <Button onClick={() => setOpen(false)}>OK</Button>
      //</Modal.Actions>
      }

    </Modal>
  </>    
  );

};

ModalConnexion.propTypes = {
  username: PropTypes.string.isRequired,
  password: PropTypes.string.isRequired,
  changeField: PropTypes.func.isRequired,
  handleLogin: PropTypes.func.isRequired,
  changeModalState: PropTypes.func.isRequired,
};

// Valeurs par défaut pour les props
ModalConnexion.defaultProps = {
  username: '',
  password: '',
};

export default ModalConnexion;
