import {
  SET_PLAYLISTS_LIST, SHOW_PLAYLIST_LANDING, HIDE_PLAYLIST_LANDING
  } from '../actions/playlist';

  export const initialState = {
    list: [],    
    // this is the landing page button state
    btnState: false,
  };

  // A noter : pour le reducer userReducer, seule la tranche user est visible !
  const reducer = (state = initialState, action = {}) => {
    switch (action.type) {  
      case SET_PLAYLISTS_LIST:
        return {
          ...state,
          list: action.playlistsList,
        };
        case SHOW_PLAYLIST_LANDING:
          console.log('je click sur le bouton playlist, et je dit cela depuis le reducer');
          return {
            ...state,
            btnState: true,
          };
          case HIDE_PLAYLIST_LANDING:
          console.log('je click sur le bouton playlist, et je dit cela depuis le reducer');
          return {
            ...state,
            btnState: false,
          }
      default:
        return state;
    }
  };

  export default reducer;
