// == IMPORT NPM
import axios from "axios";
import FormData from "form-data";

// == IMPORT ASSETS
import {
  CREATE_USER,
  GET_USERS_LIST, 
  setErrorMailCreate, 
  setUsersList,
  UPDATE_PROFILE,
} from '../actions/user';

import {  
    setProgress,
    setErrorCode
} from '../actions/musics';

import { 
  getLoggedUser
} from '../actions/user';

const usersApi = (store) => (next) => (action) =>{

  let { users : {
    name,
    email,
    password,
    picture,
    description,
    user
  }} = store.getState();

  console.log('la valeur de l\'email est : ' , email);

  if(email === 'undifined') {
    email = user.email;
  }

  if(description === ''){
    description = user.description;
  }

  const formData = new FormData();
  formData.append('picture', picture);
  formData.append('description', description);
  formData.append('email', email);
  formData.append('password', password);

  const config = {
    headers: {
      "Content-type": "multipart/form-data"
    },
    onUploadProgress: data => {

      //Set the progress value to show the progress bar
      store.dispatch(setProgress(Math.round((100 * data.loaded) / data.total)))

    }

  };

  const baseURL = 'http://denisjulien-server.eddi.cloud/projet-9-sound-clock-back/public';

  switch (action.type) {

    case CREATE_USER:
      axios.post(`${baseURL}/api/user/new` , {
        email,
        name,
        password  
      }, config
        ).then((response) => {
          console.log('Le data a était envoyé, all is good.',response);
        })
        .catch((error) => {
          store.dispatch(setErrorMailCreate(error.response.data.error))
          console.log("je suis la reponse.status",error.response.data.error);
          console.log('error nul', error);
        });
      break;

    case GET_USERS_LIST: 
      axios.get(`${baseURL}/api/users/rand`)
      .then((response) => {
          store.dispatch(setUsersList(response.data));
        })
      .catch((error) => {
          // eslint-disable-next-line no-console
          console.log('error', error);
        });
      break;

    case UPDATE_PROFILE:
        console.log('je récupére bien les info dans le middleware user : ');
        for (var pair of formData.entries()) {
            console.log(pair[0]+ ' > ' + pair[1]); 
        }
        /*
        axios.post(`${baseURL}/api/user/edit/${user.id}`,
        formData, 
        config
          ).then((response) => {
            console.log('Le user a était updaté, all is good.', response);
            store.dispatch(getLoggedUser(user.slug));
          })
          .catch((error) => {
            // eslint-disable-next-line no-console
            store.dispatch(setErrorCode(error.response.data.error))
            //* console.log("je suis la reponse.status",error.response.data.error); 
            console.log('Music Upload Error', error);
          }); 
        */
      break;
    default:
    next(action);   
  }
};
export default usersApi;
