// == IMPORT NPM 
import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import {Routes, Route} from 'react-router-dom';

// == IMPORT ASSETS
import Header from '../Header';
import Legal from '../Legal';
import Footer from '../Footer';
// import Carousel from '../Carousel';
import ParallaxMain from '../Parallax';
//import Columns from '../Columns';
import About from '../About';
import Results from '../Results';
import Contact from '../Contact';
import User from '../User';
import Error from '../Error';
import LandingPage from '../LandingPage';
//import SearchBar from '../SearchBar';
import './styles.scss';

import { getMusicsList, getTopMusicsList } from '../../actions/musics';
import { getTopUsersList, getUsersList } from '../../actions/user';
import { getPlaylistsList, getTopPlaylistsList } from '../../actions/playlist';

const App = () => {  
  const dispatch = useDispatch();

  const baseApiUrl = 'http://denisjulien-server.eddi.cloud/projet-9-sound-clock-back/public/uploads'

  useEffect(
    () => {
      dispatch(getMusicsList());
      dispatch(getUsersList());
      dispatch(getPlaylistsList());
      dispatch(getTopMusicsList());
      dispatch(getTopUsersList());
      dispatch(getTopPlaylistsList());
    },
  );




  return(

    <div className="App">
      <Header />
      <Routes>
        <Route path='/' element= { 
        <>


          <ParallaxMain />
          <LandingPage />
          {/* <Columns 
            baseApiUrl={baseApiUrl}
          /> */}
        </>
        }/>
        <Route 
          path='/legal' 
          element={<Legal baseApiUrl={baseApiUrl}/>} 
        />
        <Route 
          path='/contact' 
          element={<Contact baseApiUrl={baseApiUrl} />} 
        />
        <Route
          path='/results' 
          element={<Results baseApiUrl={baseApiUrl} />} 
        />
        <Route 
          path='/About' 
          element={<About baseApiUrl={baseApiUrl} />} 
        />
        <Route 
          path='/user/:slug' 
          element={<User 
          avatar='https://react.semantic-ui.com/images/avatar/large/matthew.png'
          baseApiUrl={baseApiUrl}
          />} 
        />
        <Route 
          path='/user' 
          element={<User 
          avatar='https://react.semantic-ui.com/images/avatar/large/matthew.png'
          baseApiUrl={baseApiUrl}
          />} 
        />
        <Route 
          path='/*' 
          element={<Error baseApiUrl={baseApiUrl} />} 
        />
      </Routes>
      <Footer />


    </div>

);
};

export default App;
