// == IMPORT NPM
import axios from "axios";
import FormData from "form-data";

// == IMPORT ASSETS
import {

    GET_MUSICS_LIST, 
    setMusicsList, 
    SEND_MUSIC,
    resetMusicState, 
    GET_TOP_MUSICS_LIST, 
    setTopMusicsList

} from '../actions/musics';

const musicApi = (store) => (next) => (action) =>{

  //const picture = "test-picture";
  //const file = "test-file";
  const { musics: { 
    title, 
    file,
    picture,
    description,
    releaseDate,
    } } = store.getState();

    const { users : { user }} = store.getState();

    const formData = new FormData();
    formData.append('title', title);
    formData.append('user', user.id);
    formData.append('file', file);
    formData.append('picture', picture);
    formData.append('releasedate', releaseDate);
    formData.append('description', description);

    const config = {
      headers: {
        "Content-type": "multipart/form-data"
      }
    };

  switch (action.type) {
    case GET_MUSICS_LIST: 
      axios.get('http://denisjulien-server.eddi.cloud/projet-9-sound-clock-back/public/api/musics')
      .then((response) => {
          store.dispatch(setMusicsList(response.data));
        })
      .catch((error) => {
          // eslint-disable-next-line no-console
          console.log('error', error);
        }); 
      break;
      case SEND_MUSIC: 
      console.log(user.id);
        axios.post(
        'http://denisjulien-server.eddi.cloud/projet-9-sound-clock-back/public/api/musics',
        formData, 
        config).then((response) => {

            console.log('Le data a était envoyé, all is good.',response);
            store.dispatch(resetMusicState());
          })
        .catch((error) => {
            // eslint-disable-next-line no-console
            console.log('Music Upload Error', error);
          }); 
        break;

      case GET_TOP_MUSICS_LIST:
        axios.get('http://denisjulien-server.eddi.cloud/projet-9-sound-clock-back/public/api/musics/top10/like') 
        .then((response) => {

          store.dispatch(setTopMusicsList(response.data));
        })
        .catch((error) => {
          // eslint-disable-next-line no-console
          console.log('error top 10', error);
        }); 
      break; 
    default:
    next(action);   
  }
};
export default musicApi;
