// == IMPORT NPM
import { useParams } from "react-router-dom";
import PropTypes from "prop-types";
import { Container, Image } from "semantic-ui-react";
import { useSelector, useDispatch } from "react-redux";

// == IMPORT ASSETS
import ModalAddMusic from "./ModalAddMusic";
import ModalUpdateProfile from "./ModalUpdateProfile";
import { setMusicField, sendMusic } from '../../actions/musics';
import Music from "../Cards/Music";
import Playlist from "../Cards/Playlist";

const User = ( { avatar, baseApiUrl }) => { 

  const { slug } = useParams();

  const dispatch = useDispatch();

  //* Modal State values for controled fields
  const modalTitle = useSelector((state) => state.musics.title);
  const modalFile = useSelector((state) => state.musics.file);
  const modalPicture = useSelector((state) => state.musics.picture);
  const modalReleaseDate = useSelector((state) => state.musics.releaseDate);
  const modalDescription = useSelector((state) => state.musics.description);
  // const modalStatus = useSelector((state) => state.musics.status);

  const userData = useSelector((state) => state.users.user);
  console.log('je suis sur la page user, voici mon data:' , userData);

  //* User Data retreival from the stat

  const changeField = (value, name) => {
    //console.log(`Je veux modifier le champ ${name} avec la valeur ${value}`);
    dispatch(setMusicField(value, name));
  };

  const handleAddMusic = () => {
    // console.log('Je veux me logger (au moins essayer)');
    dispatch(sendMusic());
  };

  const userSlug = useSelector((state) => state.users.user.slug);

  //TODO Picking up the musics results for the user from the user musics API
  const musics = useSelector((state) => state.musics.list);
  const playlists = useSelector((state) => state.playlists.list);

  return (

    <div className="user-wrapper">

      <section className="user-container">

        <div className="user__avatar">

          <Image className="user__avatar" src={avatar} size='medium' rounded />  
          {/* Modal to Upload new content, only visible if logged in */}
          {/* //TODO Make only visible if logged in */}
          <div className="content__addMusic-btn">
            <ModalUpdateProfile 
              title={modalTitle}
              file={modalFile}
              picture={modalPicture}
              releaseDate={modalReleaseDate}
              description={modalDescription}
              changeField={changeField}
              handleAddMusic={handleAddMusic}
            />     

        </div>


        </div>

        <Container className="user__header-container" fluid>

          <h1 className="user__header--name">Hello {userSlug}</h1>
          <span className="user__header--link">htt://wwww.soundclock.com/NicoPipishorts</span>
          <br />
          <br /><br />
          <span className="user__header-h2">Abonn√©es: <span className="user__header-h2--stats">227</span></span> 
          <br /><br />
          <span className="user__header-h2">Musiques: <span className="user__header-h2--stats">32</span></span>

        </Container>
        <Container fluid>

          <div className="user__description">
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent felis lacus, eleifend non gravida eget, porttitor id magna. Praesent varius tellus sed interdum vehicula. Vivamus pellentesque tristique purus eu eleifend. Fusce eget rhoncus ipsum. Ut semper et orci in hendrerit. Proin convallis ipsum ac libero pellentesque, eget luctus orci vehicula. Vivamus non lobortis dui. Praesent quis felis at nunc feugiat luctus ut id diam. Proin urna dui, lobortis in dolor in, aliquam condimentum nibh. Suspendisse vestibulum at arcu ac mollis. Sed ex sapien, volutpat id sagittis eu, dignissim ac est. 
          </div>

        </Container>

      </section>

      <section className="content-container">

      {/* Modal to Upload new content, only visible if logged in */}
      {/* //TODO Make only visible if logged in */}
      <div className="content__addMusic-btn">
        <ModalAddMusic 
          title={modalTitle}
          file={modalFile}
          picture={modalPicture}
          releaseDate={modalReleaseDate}
          description={modalDescription}
          changeField={changeField}
          handleAddMusic={handleAddMusic}
        />     
      </div>

        {/* User Music section */}
        <h1 className="content__section-title">Music</h1>
        <div className="cards__container">
          {musics.map((music) =>( 
            <Music
              key={music.id}
              name={music.title}
              date={music.releaseDate}
              image={music.picture}
              description={music.description}
              song={music.file}
              nbLike={music.nbLike} 
              nbListened={music.nbListened} 
              baseApiUrl={baseApiUrl}   
              />
          ) ) }
        </div>
        {/* User Playlist section */}
        <h1 className="content__section-title">Playlists</h1>
        <div className="cards__container">
          {playlists.map((playlist) =>( 
            <Playlist
              key={playlist.id}
              name={playlist.title}
              date={playlist.createdAt}
              image={playlist.picture}
              description={playlist.description}
              nbLike={playlist.nbLike} 
              baseApiUrl={baseApiUrl}   
              />
          ) ) }
        </div>

      </section>

    </div>

  );

};

User.propTypes = {
  avatar: PropTypes.string.isRequired,
}


export default User;
