import {
    GET_MUSICS_LIST, 
    setMusicsList, 
    SEND_MUSIC,
    resetMusicState
} from '../actions/musics';

const musicApi = (store) => (next) => (action) =>{

  //const picture = "test-picture";
  //const file = "test-file";
  const { musics: { 
    title, 
    file,
    picture,
    description,
