import React from 'react';
import { List, Dropdown, Button, Image} from 'semantic-ui-react'
import { useSelector } from 'react-redux';
import ReactAudioPlayer from 'react-audio-player';

import './styles.scss';


const LandingPage = () => {

    const musics = useSelector((state) => state.musics.list);
    //console.log('Object sur view column', musics);

    const users = useSelector((state) => state.users.list);
    //console.log('Object sur view column', users);  

    const playlists = useSelector((state) => state.playlists.list);
    //console.log('Object sur view column', playlists);




    return (
        <>
        <Button>TOP 10 </Button>

        <Dropdown text='Artistes'>
            <Dropdown.Menu>
                <Dropdown.Item text='Playlist' />
                <Dropdown.Item text='Musiques' />
            </Dropdown.Menu>
        </Dropdown>

        <Dropdown text='Genres'>
            <Dropdown.Menu>
                <Dropdown.Item text='Country' />
                <Dropdown.Item text='Variété' />
                <Dropdown.Item text='Rap' />
            </Dropdown.Menu>
        </Dropdown>

        <Button>Découvertes </Button>

        <Image size='big' src='https://m.media-amazon.com/images/I/81B1nzZcddL._SL1444_.jpg' fluid />


    <List divided relaxed className='list'>
    {musics.slice(0,10).map((music) => (
        <List.Item>
        <Image avatar src={music.picture} />
                <List.Content>
                    <List.Header>{music.title}</List.Header>
                    <List.Header></List.Header>

                    <List.Description>{music.description}</List.Description>
                    <ReactAudioPlayer
                        src={music.file}

                        controls
                    />
                </List.Content>
        </List.Item>
    ))}

  </List>
  </>
    );

};

export default LandingPage;
