import {
  SET_USER_FIELD, 
  SET_USERS_LIST, 
  SAVE_USER, 
  CREATE_USER, 
  LOGOUT,
  SAVE_USER_WITH_SLUG,
  SAVE_LOGGED_USER,
  SET_ERROR_MAIL_CREATE
} from '../actions/user';

export const initialState = {
  // l'utilisateur est-il loggé
  logged: false,
  // token de l'utilisateur 
  token: '',
  // email de l'utilisateur
  username: '',
  // object with user deails
  loggedUser: [],
  // password de l'utilisateur
  password: '',
  //email
  email:'',
  //description
  decription:'',
  // Users List
  // user info when card clicked 

  list: [],
  //Open or Close Login Modal
  loginButton: false,
  //Open or Close Create Modal
  createButton: false,

  errorCodeMail:'',
};

// A noter : pour le reducer userReducer, seule la tranche user est visible !
const reducer = (state = initialState, action = {}) => {
  switch (action.type) {
    case CREATE_USER:
      return{
        ...state,
        [action.name]: action.value,

      }
    case SET_USER_FIELD:
      return {
        ...state,
        [action.name]: action.value,
      };
    case SET_USERS_LIST:
      return {
        ...state,
        list: action.usersList,
      };   
    case SAVE_USER:
      // console.log('etape : 6, je passe le saveUser dans le reducer');
      return {
        ...state,
        logged: true,        
        email: action.user.username,
        token: action.user.token,
        user: action.user,
      };
    case SAVE_USER_WITH_SLUG:
      console.log('etape : 5, je passe le saveUser dans le reducer', action.user);
      return {
        ...state,
        user: action.user,
      };
    case SAVE_LOGGED_USER:
      // console.log('etape : 5, je passe le saveUser dans le reducer');
      return {
        ...state,
        logged: true,      
        email: action.user.username,
        loggedUser: action.user,
      };
    case LOGOUT: 
      //* Empty local storage
      localStorage.removeItem('userToken'); 
      return {
        logged: false,
        toDashboard: false,
        username: '',
        password: '',
        email: '',
        list: '',
        loggedUser: '',
        decription: '',
        token: '',  
      };

    case SET_ERROR_MAIL_CREATE:
      console.log('je recupere le code')
      return {
        ...state,
        errorCodeMail: action.code,
      }

    default:
      return state;
  }
};

export default reducer;
