import React, { useState } from 'react';
import {NavLink} from 'react-router-dom';
import Moment from 'moment';
import { List, Button, Image} from 'semantic-ui-react'
import { useSelector, useDispatch } from 'react-redux';

import { showMusicLanding, hideMusicLanding } from '../../actions/musics';
import { showPlaylistLanding, hidePlaylistLanding } from '../../actions/playlist';
import { showUserLanding, hideUserLanding } from '../../actions/user';

import './styles.scss';


const LandingPage = () => {
    const dispatch = useDispatch();

    const musics = useSelector((state) => state.musics.topList);
    let musicsState = useSelector((state) => state.musics.isActive);
    console.log('Object sur view column musics', musics);

    const users = useSelector((state) => state.users.topList);
    let usersState = useSelector((state) => state.users.isActive);
    console.log('Object sur view column users ', users);  

    const playlists = useSelector((state) => state.playlists.topList);
    let playlistsState = useSelector((state) => state.playlists.isActive);
    console.log('Object sur view column playlist', playlists);


    const handleMusic = () => {

        dispatch(showMusicLanding());
        dispatch(hidePlaylistLanding());
        dispatch(hideUserLanding());        

    }
    const handleArtiste = () => {
        dispatch(showUserLanding())
        dispatch(hideMusicLanding())
        dispatch(hidePlaylistLanding())        
    }
    const handlePLaylist = () => {        
        dispatch(showPlaylistLanding())
        dispatch(hideUserLanding())
        dispatch(hideMusicLanding())        
    }



    return (

        <div className='container'>



        <div className='container-main'>

        <Button.Group  className='buttons__list'>
            <Button onClick={handleMusic} className='buttons__list--music'  >
                Musics
            </Button>
            <Button onClick={handleArtiste} className='buttons__list--artiste'>
                Artistes
            </Button>
            <Button onClick={handlePLaylist} className='buttons__list--playlist'>
                Playlist
            </Button>
        </Button.Group>


            { musicsState && 
            <>
                <List divided relaxed className='container__list' >
                {musics.map((music) => (
                    <List.Item className='container__list__item'>
                    <Image circular  src={music.picture}  className='container__list__item__img'></Image> 
                            <List.Content className='container__list__item__content'>
                                <List.Header>{music.title}</List.Header>
                                <List.Content>{music.description}</List.Content>
                                <List.Description>{Moment(music.release_date).format('D MMM Y')}</List.Description>
                                <audio
                                    className='audioplayer'
                                    controls
                                    use-credentials
                                    src={music.file}>
                                        Your browser does not support the
                                        <code>audio</code> element.
                                    </audio>
                            </List.Content>
                    </List.Item>
                ))}
                </List>
            </>
            }
            { usersState && 
            <>

                <List divided relaxed  className='container__list'>
                {users.map((user) => (
                    <List.Item className='container__list__item'>
                    <Image circular  src={user.picture} className='container__list__item__img'/>
                            <List.Content className='container__list__item__content'>
                                <List.Header>{user.name}</List.Header>
                                <List.Description>{user.description}</List.Description>
                            </List.Content>
                    </List.Item>
                ))}
                </List>
            </>
            }
            { playlistsState &&
            <>

                <List divided relaxed className='container__list'>
                {playlists.map((playlist) => (
                    <List.Item className='container__list__item'>
                    <Image circular  src={playlist.picture} className='container__list__item__img'  />
                            <List.Content className='container__list__item__content'>
                                <List.Header>{playlist.title}</List.Header> 
                                <List.Description>{playlist.description}</List.Description> 
                            </List.Content>
                    </List.Item>
                ))}
                </List>   
            </>

            }

        </div>

    <div className='container-image'>
        <Image className="img" size='big' src='https://m.media-amazon.com/images/I/81B1nzZcddL._SL1444_.jpg' /> 

    </div>

    </div>
    );

};

export default LandingPage;
