import {
  SET_MUSIC_FIELD, 
  SET_MUSICS_LIST, 
  SHOW_MUSIC_LANDING, 
  HIDE_MUSIC_LANDING,
  RESET_MUSIC_STATE,
  SET_TOP_MUSICS_LIST,
} from '../actions/musics';

export const initialState = {
  // title of the file to upload
  title: '',
  // file to upload
  file: '',
  // cover photo for the song
  picture: '',
  // description of music to upload
  description: '',
  // initial release date of the song
  releaseDate: '',
  // is the song visible or not
  status: '1',
  // this is the landing page button state
  btnState: false,

  isActive: false,

  // empty object for storing the musics list
  list : [],
  // empty object for storing the top musics list
  topList : [],
};

// A noter : pour le reducer userReducer, seule la tranche user est visible !
const reducer = (state = initialState, action = {}) => {
  switch (action.type) {
    case SET_MUSIC_FIELD:
      return {
        ...state,
        // pour définir dynamiquement la propriété d'un objet, on utilise la
        // notation "crochet"
        [action.name]: action.value,
      };

    case SET_MUSICS_LIST:
      return {
        ...state,
        list: action.musicList,
      };      
    case RESET_MUSIC_STATE:
      return {
        ...state,
        title: '',
        file: '',
        picture: '',
        description: '',
        releaseDate: ''
      }
    case SHOW_MUSIC_LANDING:
      console.log('je click sur le bouton music, et je dit cela depuis le reducer');
      return {
        ...state,
        btnState: true,
      }
      case HIDE_MUSIC_LANDING:
      console.log('je click sur le bouton music, et je dit cela depuis le reducer');
      return {
        ...state,
        btnState: false,
      };

    case SET_TOP_MUSICS_LIST:
      return {
        ...state,
        topList: action.musicList,
      };  

    case SHOW_MUSIC_LANDING:

      return {
        ...state,
        isActive: true,
      }
    case HIDE_MUSIC_LANDING:

      return {
        ...state,
        isActive: false,
      }
    default:
      return state;
  }
};

export default reducer;
