import React from 'react';
import PropType from "prop-types";
import { Header, Modal, Button, Icon, Form, Input, TextArea } from "semantic-ui-react";
import './styles.scss';

const ModaleUpdateProfile = ({
    username,
    picture,
    description,
    changeFieldUpdate,
    handleUpdateProfile
}) => {
    const [open, setOpen] = React.useState(false)

    const handleChange = (e) => {
        changeFieldUpdate(e.target.value, e.target.name);
    };
    const handleFiles =(evt) => {
        changeFieldUpdate(evt.target.files[0], evt.target.name);
    };

    const handleSubmit = (evt) => {
        evt.preventDefault();
        handleUpdateProfile();
    };

    return (

      <Modal
        centered={false}
        open={open}
        onClose={() => setOpen(false)}
        onOpen={() => setOpen(true)}
        trigger={
          <Button 
            className='user__modifyUser--btn'
          >
          <Icon name="sync" />
            Modifier votre compte
          </Button>
          }
      >
        <Header color='teal' size='large'>Modifier votre compte:</Header>
        <Modal.Content>

          <Form className="upload-form" onSubmit={handleSubmit} >

            <Form.Field>
              <Input
                label={{ icon: 'asterisk', color: 'teal'  }}
                labelPosition='left corner'
                placeholder='Votre pseudo'
                size='huge'
                type='text'
                fluid 
                onChange={handleChange}
                name='username'
                value={username}
              />
            </Form.Field>

            <TextArea 
              placeholder='Modifier votre description' 
              className='file-textArea'
              name='description'
              onChange={handleChange}
              value={description}

            />
            <br /><br />
            <div className="file-input-container">
              <Button className="file-input__btn" as="label" htmlFor="picture" type="button" color='teal'>
                <Icon name="file image outline" size='large'/>
                Modifier votre image de profil.
              </Button>
              <input 
                type="file" 
                id="picture" 
                className="file-input__hidden" 
                onChange={handleFiles}
                name='picture'
              />
            </div>
            <br />

            <br /><br />
            <div className="form__submit-container"> 
              <Button color='teal' size='huge' type='submit'>Modifier</Button>
            </div>

          </Form>

        </Modal.Content>
      </Modal>

    );
};

export default ModaleUpdateProfile;
