// == IMPORT NPM
import axios from "axios";

// == IMPORT ASSETS
import {
    GET_PLAYLISTS_LIST , GET_TOP_PLAYLISTS_LIST, setPlaylistsList, setTopPlaylistsList
} from '../actions/playlist';

const playlistApi = (store) => (next) => (action) =>{

  switch (action.type) {

    case GET_PLAYLISTS_LIST : 
      axios.get('http://denisjulien-server.eddi.cloud/projet-9-sound-clock-back/public/api/playlists')
      .then((response) => {
          // console.log('La liste des musiques',response);
          store.dispatch(setPlaylistsList(response.data));
        })
      .catch((error) => {
          // eslint-disable-next-line no-console
          console.log('error', error);
        });
      break;
    case GET_TOP_PLAYLISTS_LIST: 
    axios.get('http://denisjulien-server.eddi.cloud/projet-9-sound-clock-back/public/api/playlists/top10/like')
    .then((response) => {
      console.log(response.data);
      store.dispatch(setTopPlaylistsList(response.data));
    })
    .catch((error) => {
      console.log('error playlist', error);
    });
    break;  
    default:
    next(action);   
  }
};
export default playlistApi;
