// == IMPORT NPM
import React from "react";
import PropType from "prop-types";
import { Header, Modal, Button, Icon, Form, Input, TextArea } from "semantic-ui-react";

// == IMPORT ASSETS
import './styles.scss'

const ModalAddMusic = ({
  title,
  file,
  picture,
  releaseDate,
  description,
  changeField,
  handleAddMusic
}) => { 

  const [open, setOpen] = React.useState(false)

  const handleChange = (evt) => {
    changeField(evt.target.value, evt.target.name);
  };

  const handleFiles =(evt) => {
    changeField(evt.target.files[0], evt.target.name);
  };

  const handleSubmit = (evt) => {
    evt.preventDefault();
    handleAddMusic();
  };

  return(

    <Modal
    centered={false}
    open={open}
    onClose={() => setOpen(false)}
    onOpen={() => setOpen(true)}
    trigger={
      <Button 
        color="teal" 
        size="big"
      >
        <Icon name="add" />
        Ajouter une musique.
      </Button>
      }
  >
    <Header color='teal' size='large'>Ajoutez une musique a votre compte:</Header>
    <Modal.Content>

      <Form className="upload-form" onSubmit={handleSubmit}>
        <Form.Field>
          <Input
            label={{ icon: 'asterisk', color: 'teal'  }}
            labelPosition='left corner'
            placeholder='Titre du Morceau'
            size='huge'
            type='text'
            fluid 
            onChange={handleChange}
            name='title'
            value={title}
          />
        </Form.Field>
        <Form.Field>
          <Input
            label={{ icon: 'asterisk', color: 'teal'  }}
            labelPosition='left corner'
            placeholder='Date de sortie de la chanson: JJ/MM/AAAA'
            size='huge'
            type='text'
            fluid 
            onChange={handleChange}
            name='releaseDate'
            value={releaseDate}
          />
        </Form.Field>
        {/*
        //TODO This will be auto populated by the users label
        <Form.Field>
          <Input
            label={{ icon: 'asterisk', color: 'teal'  }}
            labelPosition='left corner'
            placeholder='Label'
            size='huge'
            type='text' 
            fluid 
            required
          />
        </Form.Field>
        */}
        <TextArea 
          placeholder='Description' 
          className='file-textArea'
          onChange={handleChange}
          name='description'
          value={description}
        />
        <br /><br />
        <div className="file-input-container">
          <Button className="file-input__btn" as="label" htmlFor="picture" type="button" color='teal'>
            <Icon name="file image outline" size='large'/>
            Choisir une image de couverture.
          </Button>
          <input 
            type="file" 
            id="picture" 
            className="file-input__hidden" 
            onChange={handleFiles}
            name='picture'
          />
        </div>
        <br />
        <div className="file-input-container">
          <Button className="file-input__btn" as="label" htmlFor="file" type="button" color='teal'>
            <Icon name="file audio outline" size='large'/>
            Choisissez votre chanson.
          </Button>
          <input 
            type="file" 
            id="file" 
            className="file-input__hidden" 
            onChange={handleFiles}
            name='file'
          />
        </div>
        <br /><br />
        <div className="form__submit-container"> 
          <Button color='teal' size='huge' type='submit'>Uploader</Button>
        </div>

      </Form>

    </Modal.Content>
  </Modal>

  );

};

export default ModalAddMusic;
