// IMPORT NPM
import { useSelector } from 'react-redux';
import React from "react";
import { Button, Header, Icon, Modal } from "semantic-ui-react";
import PropTypes from 'prop-types';

// == IMPORT ASSETS
import Music from '../Cards/Music';
import User from '../Cards/User';
import Playlist from '../Cards/Playlist';
import './styles.scss';

const Columns = ( { 
  baseApiUrl,
  handleOnClickUser
 }) => {

  const [open, setOpen] = React.useState(false)

  const musics = useSelector((state) => state.musics.list);

  const users = useSelector((state) => state.users.list)

  const playlists = useSelector((state) => state.playlists.list);

return(

  <section className="columns">

    <div className='columns-container columns-container__col-1'>

      <div className='columns-container__title'>
        Top 10 Music
      </div>
      {musics.map((music) => (
        <Music
          key={music.id}
          name={music.title}
          date={music.releaseDate}
          image={music.picture}
          description={music.description}
          song={music.file}
          nbLike={music.nbLike} 
          nbListened={music.nbListened} 
          baseApiUrl={baseApiUrl}   
          />
      ))}
    </div>

    {/* //* Scrollbar hide placeholder  
    <div className="columns-container__placeholder"></div>
    */ }

    <div className='columns-container columns-container--columns columns-container__col-2'>

      <span className="columns-container__title" >Top 10 Artistes</span>
      {users.map((user) => (
      //<NavLink to={`/user/${user.slug}`} >
        <User
          key={user.id}
          certification={user.certification}
          date={user.createdAt}
          description={user.description}
          email={user.email}
          label={user.label}
          name={user.name}
          slug={user.slug}
          image={user.picture}
          baseApiUrl={baseApiUrl}   
          handleOnClickUser = {handleOnClickUser}
          />
         // </NavLink>
      ))}
    </div>

    {/* //* Scrollbar hide placeholder  
    <div className="columns-container__placeholder"></div>
    */ }

    <div className='columns-container columns-container__col-3'>
      <span className="columns-container__title" >Top 10 Playlist</span>
      {playlists.map((playlist) => (

        <Modal
          closeIcon
          open={open}
          trigger={<Button>Show Modal</Button>   
            }
        >
          <Header icon='archive' content='Archive Old Messages' />
          <Modal.Content>
            <p>
              Your inbox is getting full, would you like us to enable automatic
              archiving of old messages?
            </p>
          </Modal.Content>
          <Modal.Actions>
            <Button color='red' onClick={() => setOpen(false)}>
              <Icon name='remove' /> No
            </Button>
            <Button color='green' onClick={() => setOpen(false)}>
              <Icon name='checkmark' /> Yes
            </Button>
          </Modal.Actions>
        </Modal>

      ))}
    </div>

    {/* //* Scrollbar hide placeholder  
    <div className="columns-container__placeholder"></div>
    */ }

  </section> 

  );
};

Columns.propTypes = {
  baseApiUrl: PropTypes.string.isRequired,
}

export default Columns;
