import {
  SET_PLAYLISTS_LIST, 
  SHOW_PLAYLIST_LANDING, 
  HIDE_PLAYLIST_LANDING, 
  SET_TOP_PLAYLISTS_LIST
  } from '../actions/playlist';

  export const initialState = {
    list: [],    
    // this is the landing page button state
    btnState: false,

    isActive: false,
    // empty object for storing the top playlist list
    topList : [],
  };

  // A noter : pour le reducer userReducer, seule la tranche user est visible !
  const reducer = (state = initialState, action = {}) => {
    switch (action.type) {  
      case SET_PLAYLISTS_LIST:
        return {
          ...state,
          list: action.playlistsList,
        };
        case SET_TOP_PLAYLISTS_LIST:
          return {
            ...state,
            topList: action.playlistsList
          }
        case SHOW_PLAYLIST_LANDING:

          return {
            ...state,
            isActive: true,
          };
          case HIDE_PLAYLIST_LANDING:

          return {
            ...state,
            isActive: false,
          }
      default:
        return state;
    }
  };

  export default reducer;
