// IMPORT NPM
import React from 'react';
import { useParams } from 'react-router-dom';
import { useSelector } from 'react-redux';

// IMPORT ASSETS
import './styles.scss';

const Playlist = () => {

  const playlists = useSelector((state) => state.playlists.list);

  const { playlist } = useParams();

  const details = playlists.find(playlists => playlists.name === playlist);
  console.log(details);

  return(
    <section className='playlist__container'>

      <div>
        <div className="user__avatar" style={{"backgroundImage": `url(${details.picture})` }}></div>
        <h1 className='playlist__h1'>Playlist: <span className='playlist__h1--span'>{ playlist }</span></h1>
      </div>

    </section>
  );
};

export default Playlist;
