// IMPORT NPM
import { useSelector } from 'react-redux';
import { NavLink } from 'react-router-dom';
import PropTypes from 'prop-types';

// == IMPORT ASSETS
import Music from '../Cards/Music';
import User from '../Cards/User';
import Playlist from '../Cards/Playlist';
import './styles.scss';

const Columns = ( { baseApiUrl }) => {

  const musics = useSelector((state) => state.musics.list);

  const users = useSelector((state) => state.users.list);

  const playlists = useSelector((state) => state.playlists.list);

return(

  <section className="columns">

    <div className='columns-container columns-container__col-1'>

      <div className='columns-container__title'>
        Top 10 Music
      </div>
      {musics.map((music) => (
        <Music
          key={music.id}
          name={music.title}
          date={music.releaseDate}
          image={music.picture}
          description={music.description}
          song={music.file}
          nbLike={music.nbLike} 
          nbListened={music.nbListened} 
          baseApiUrl={baseApiUrl}   
          />
      ))}
    </div>

    {/* //* Scrollbar hide placeholder  
    <div className="columns-container__placeholder"></div>
    */ }

    <div className='columns-container columns-container__col-2'>

      <span className="columns-container__title" >Top 10 Artistes</span>
    </div>

    {/* //* Scrollbar hide placeholder  
    <div className="columns-container__placeholder"></div>
    */ }

    <div className='columns-container columns-container__col-3'>
      <span className="columns-container__title" >Top 10 Playlist</span>
      {playlists.map((playlist) => (
        <Playlist
          key={playlist.id}
          name={playlist.name}
          date={playlist.createdAt}
          image={playlist.picture}
          description={playlist.description}
          nbLike={playlist.nbLike} 
          baseApiUrl={baseApiUrl}   
          />
      ))}
    </div>

    {/* //* Scrollbar hide placeholder  
    <div className="columns-container__placeholder"></div>
    */ }

  </section> 

  );
};

Columns.propTypes = {
  baseApiUrl: PropTypes.string.isRequired,
}

export default Columns;
