// IMPORT NPM
import React from "react";
import { Icon, Button  } from "semantic-ui-react";
import { NavLink } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";

// IMPORT ASSETS
import SearchBar from "../SearchBar";
import ModalCreate from "./ModalCreate";
import ModalConnexion from "./ModalConnexion";
import { setUserField, login, changeModalState, creatUser, logout } from '../../actions/user';
import { search, setResearchField } from "../../actions/research";
import Logo from './logo.png';
import "./styles.scss";

const NavBar = ({research}) => {

  const dispatch = useDispatch();

  // on récupère depuis le store les valeurs courantes
  // d'email et password
  const username = useSelector((state) => state.users.username);
  const password = useSelector((state) => state.users.password);
  const email = useSelector((state) => state.users.email);

  const logged = useSelector((state) => state.users.logged);
  console.log(logged);

  const changeField = (value, name) => {
    dispatch(setUserField(value, name));
  };

  const handleLogin = () => {
    dispatch(login());
  };
  const handleCreate = () => {
    dispatch(creatUser())
  }
  const handleLogout = () => {
    dispatch(logout());
  }
  const changeFieldSearch = (value, name) => {
    dispatch(setResearchField(value, name));
  };
  const handleSearch = () => {
    dispatch(search());
  }
  /* const handleChange = (evt) => {
    changeField(evt.target.value, evt.target.name);
    console.log(evt.target.value + 'value', evt.target.name + 'name' )
  }; */
  return( 
    <nav className="navbar__container">

      <div className="">
      <NavLink to="/">       
      </NavLink>  
      </div>

      <div className="searchbar-container">
        <SearchBar
          research={research}
          changeField={changeFieldSearch}
          handleSearch={handleSearch}
        />
      </div>

      <div className="navbar__buttons-container">
        {/* Account Creation and Login button, only visible if not connected to account */}

        {
          logged === false && 
        <>
          <div>
          <ModalCreate 
            username={username}
            password={password}
            email={email}
            changeField={changeField}
            handleCreate={handleCreate}
            changeModalState={changeModalState}
          />
        </div>
        <div>
          <ModalConnexion 
            username={username}
            password={password}
            changeField={changeField}
            handleLogin={handleLogin}
            changeModalState={changeModalState}
          />

        </div>
        </>
        }
        {
          logged && 

          <>
          <Button color="teal" animated='fade'>
          <Button.Content visible>Your are logged</Button.Content>
          <Button.Content hidden>{username}</Button.Content>
          </Button>

          <Button color="teal" size="big" onClick={handleLogout}>
          <Icon name="user circle" />
          Logout
        </Button>
        </>   

        }



        {/* Hidden button that will show only once logged in
        <div>
        <Button color="teal" size="big">
          <Icon name="user circle" />
          Login
        </Button>
        </div>
        */}
      </div>

    </nav>
  );

}

export default NavBar;
