        )
        .then(
          (response) => {
          // une fois les favoris obtenus, on demande leur sauvegarde dans le state
            store.dispatch(saveFavorites(response.data.favorites));
          },
        ).catch(
          () => console.log('error recup favoris'),
        );
      next(action);
      break;
    }
    */
 /*    case LOGOUT:
      // au logout, on supprime le token de l'instance
      axiosInstance.defaults.headers.common.Authorization = null;
      next(action);
      break;
    default:
      next(action); */


  }
};

export default authMiddleWare;
