// == IMPORT NPM
import { Card, Image } from 'semantic-ui-react';
import PropTypes from 'prop-types';

// == IMPORT ASSETS
import "./styles.scss";

const ColumnsCard = ( {
  image, 
  name, 
  label, 
  description,
  }) => (

  <Card className='cards-container'>
  <Image 
    className='cards-image'
    src={image} 
  />
  <Card.Content>
    <Card.Header>{name}</Card.Header>
    <Card.Description>
      {label}
    </Card.Description>
    <Card.Description>
      {description}
    </Card.Description>
    <br />
  </Card.Content>
</Card>

);

export default ColumnsCard;
