import {
  SET_USER_FIELD, 
  SET_USERS_LIST, 
  SAVE_USER, 
  CHANGE_MODAL_STATE, 
  CREATE_USER, 
  LOGOUT,
  SAVE_USER_WITH_SLUG,
} from '../actions/user';

export const initialState = {
  // l'utilisateur est-il loggé
  logged: false,
  // token de l'utilisateur 
  token: '',
  // email de l'utilisateur
  username: '',
  // object with user deails
  user: [],
  // password de l'utilisateur
  password: '',
  //email
  email:'',
  //description
  decription:'',
  // Users List
  list: [],
  //Open or Close Login Modal
  loginButton: false,
  //Open or Close Create Modal
  createButton: false,
};

// A noter : pour le reducer userReducer, seule la tranche user est visible !
const reducer = (state = initialState, action = {}) => {
  switch (action.type) {
    case CREATE_USER:
      return{
        ...state,
        [action.name]: action.value,

      }
    case SET_USER_FIELD:
      return {
        ...state,
        [action.name]: action.value,
      };
    case SET_USERS_LIST:
      return {
        ...state,
        list: action.usersList,
      };
    case CHANGE_MODAL_STATE:
      return{
        ...state,
        [action.name]: !state[action.name],
      };     
    case SAVE_USER:
      console.log('etape : 5, je passe le saveUser dans le reducer');
      return {
        ...state,
        logged: true,        
        email: action.user.username,
        token: action.user.token,
        user: action.user,
      };
    case SAVE_USER_WITH_SLUG:
      //console.log('etape : 5, je passe le saveUser dans le reducer');
      return {
        ...state,       
        user: action.user,
      };
    case LOGOUT:
      return {
        logged: false,
        username: '',
        password: '',
        email: '',
        list: '',
        decription: '',
        token: '',  
      };

    default:
      return state;
  }
};

export default reducer;
