// IMPORT NPM
import React from "react";
import { Icon, Button  } from "semantic-ui-react";
import { NavLink, useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";

// IMPORT ASSETS
import SearchBar from "../SearchBar";
import ModalCreate from "./ModalCreate";
import ModalConnexion from "./ModalConnexion";
import { setUserField, login, creatUser, logout } from '../../actions/user';
import { search, setResearchField } from "../../actions/research";
import Logo from './logo-v.png';
import "./styles.scss";

const NavBar = ({research}) => {

  const dispatch = useDispatch();

  const navigate = useNavigate();

  // on récupère depuis le store les valeurs courantes
  // d'email et password
  const username = useSelector((state) => state.users.username);
  const password = useSelector((state) => state.users.password);
  const userData = useSelector((state) => state.users.user);

  const logged = useSelector((state) => state.users.logged);
  const errorCode = useSelector((state) => state.users.errorCodeMail);


  const changeField = (value, name) => {
    dispatch(setUserField(value, name));
  };

  const handleLogin = () => {
    dispatch(login());
    // console.log('etape : 2, je passe par le handleLogin vers le middleware login');
  };
  const handleCreate = () => {
    dispatch(creatUser())
  }
  const handleLogout = () => {
    dispatch(logout());    
    navigate('/');
    window.location.reload(); 
  }
  const changeFieldSearch = (value, name) => {
    dispatch(setResearchField(value, name));
  };
  const handleSearch = () => {
    dispatch(search());
  }

  return( 
    <nav className="navbar__container">

      <NavLink to="/">
      <div className="navbar__logo" style={{ "backgroundImage": `url(${Logo})` }} />
      </NavLink>  

      <div className="searchbar-container">
        <SearchBar
          research={research}
          changeField={changeFieldSearch}
          handleSearch={handleSearch}
        />

      </div>

      <div className="navbar__buttons-container">
        {/* Account Creation and Login button, only visible if not connected to account */}

        {(!logged) && 
        <>
          <div>
          <ModalCreate 
            errorCode={errorCode}
            username={username}
            password={password}
            email={userData.email}
            changeField={changeField}
            handleCreate={handleCreate}
          />
        </div>
        <div>
          <ModalConnexion 
            username={username}
            password={password}
            changeField={changeField}
            handleLogin={handleLogin}
          />

        </div>
        </>
        }
        {(logged) && 

          <>
          <Button as={NavLink} to={`/user/${userData.slug}`} color="orange" size="big">
            <Icon name="user circle" />
            {userData.name}
          </Button>

          <Button color="teal" animated='fade' size="big" onClick={handleLogout}>
            <Button.Content visible><Icon name='log out' /></Button.Content>
            <Button.Content hidden><Icon name='lock' /></Button.Content>
          </Button>
        </>    
        }



        {/* Hidden button that will show only once logged in
        <div>
        <Button color="teal" size="big">
          <Icon name="user circle" />
          Login
        </Button>
        </div>
        */}
      </div>

    </nav>
  );

}

export default NavBar;
