// == IMPORT NPM
import React from "react";
import { Modal, Button, Icon, Input, Form, Message,Header } from "semantic-ui-react";
import PropTypes from 'prop-types';

const ModalConnexion = ({  
  email,
  password,
  changeField,
  handleLogin,
}) => { 

  const handleChange = (evt) => {
    changeField(evt.target.value, evt.target.name);
  };

  const handleSubmit = (evt) => {
    evt.preventDefault();
    handleLogin();
    // console.log('etape : 1, je passe par le handleSubmit > handlelogin');
  };

  const [open, setOpen] = React.useState(false)

  return(
    <>   
      <Modal
      centered={false}
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      trigger={ 
      <Button name="loginButton" color='teal' className="navbar__buttons">
        <Icon name="sign-in" />
        Sign in
      </Button>
      }
      >
      <Header size='large' color="teal">Connectez vous à votre compte</Header>
      <Modal.Content>
        <Modal.Description>
          <Form  onSubmit={handleSubmit}>
            <Form.Field>
            <Input
              className="navbar__login-btns"
              label={{ icon: 'asterisk', color: 'teal'  }}
              labelPosition='left corner'
              icon={<Icon name='user circle' color='teal' />}
              iconPosition='left'
              placeholder='adresse email'
              size='huge'
              type='email'
              required
              fluid 
              onChange={handleChange}
              name='email'
              value={email}
            />
            </Form.Field>
            <Form.Field>
            <Input
              className="navbar__login-btns"
              label={{ icon: 'asterisk', color: 'teal'  }}
              labelPosition='left corner'
              icon={<Icon name='lock' color='teal' />}
              iconPosition='left'
              placeholder='mot de passe'
              size='huge'
              type='password'
              required
              fluid 
              onChange={handleChange}
              name='password'
              value={password}
            />
            </Form.Field>
            {// <Form.Field>
              //<Checkbox label='I agree to the Terms and Conditions' />
            // </Form.Field>
            }
            <Button   color='teal' fluid size='large'>
              Login
            </Button>
          </Form>
        </Modal.Description>
      </Modal.Content>
      <Message>
          Nouveau ici ? <a href='/#'>Creer un compte</a>
        </Message>
      {
      //<Modal.Actions>
      //  <Button onClick={() => setOpen(false)}>OK</Button>
      //</Modal.Actions>
      }

    </Modal>
  </>    
  );

};

export default ModalConnexion;
