// ACTION TYPE SET_USER_FIELD
export const SET_USER_FIELD = 'SET_USER_FIELD';

// ACTION CREATOR setUserField
export const setUserField = (value, name) => ({
  type: SET_USER_FIELD,
  name,
  value,
});

// ==== Action pour modifier l'état des modal
// ACTION TYPE CHANGE_MODAL_STATE
export const CHANGE_MODAL_STATE = 'CHANGE_MODAL_STATE';

// ACTION CREATOR changeModalState
export const changeModalState = (name) => ({
  type: CHANGE_MODAL_STATE,
  name,
});

// ACTION TYPE LOGIN
export const LOGIN = 'LOGIN';

// ACTION CREATOR login
export const login = () => ({
  type: LOGIN,
});

// ACTION TYPE GET_USERS_LIST
export const GET_USERS_LIST = 'GET_USERS_LIST';

// ACTION CREATOR getUsersList
export const getUsersList = () => ({
  type: GET_USERS_LIST,
});

// ACTION TYPE SET_USERS_LIST
export const SET_USERS_LIST = 'SET_USERS_LIST';

// ACTION CREATOR setUsersList
export const setUsersList = (usersList) => ({
  type: SET_USERS_LIST,
  usersList,
});

// ACTION TYPE SAVE_USER
export const SAVE_USER = 'SAVE_USER';

// ACTION CREATOR saveUser
export const saveUser = (user) => ({
  type: SAVE_USER,
  user,
});
// ACTION TYPE SHOW_MUSIC_LANDING
export const SHOW_USER_LANDING = 'SHOW_USER_LANDING';
// Set music fields in the upload new music modal
export const showUserLanding = () => ({
  type: SHOW_USER_LANDING,
});
// ACTION TYPE SHOW_MUSIC_LANDING
export const HIDE_USER_LANDING = 'HIDE_USER_LANDING';
// Set music fields in the upload new music modal
export const hideUserLanding = () => ({
  type: HIDE_USER_LANDING,
});
