// == IMPORT NPM
import React, { useState } from "react";
import {Header, Modal, Button, Icon, Form, Input, Message } from "semantic-ui-react";
import './styles.scss'

const ModalCreate = ({
  username,
  email,
  password,
  changeField,
  handleCreate,
  errorCode
}) => { 

  const [submitstate, setSubmitstate] = useState(true);

  const handleChange = (evt) => {
    changeField(evt.target.value, evt.target.name);
  };

  const handleSubmit = (evt) => {
    evt.preventDefault();
    handleCreate();
  }; 

  const verifPassword = (evt) => {
    const password2 = document.getElementById('password_verif').value;
    if(password === password2){
      setSubmitstate(false);
    }
  }
  console.log('l etat du button :', submitstate);

  const [open, setOpen] = React.useState(false)
  return (
  <>    
    <Modal
    centered={false}
    onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      trigger={
      <Button  name="createButton" color='teal' className="navbar__buttons" >
      <Icon name="add user" />
      Create Account
      </Button>
      }
      >
      <Header size='large' color="teal">Creation de votre compte</Header>
      <Modal.Content>

        <Form className="modal__creat__form" onSubmit={handleSubmit}>
        <Form.Field>
          <Input
            className="navbar__login-btns"
            label={{ icon: 'asterisk', color: 'teal'  }}
            labelPosition='left corner'
            icon={<Icon name='user circle' color='teal' />}
            iconPosition='left'
            placeholder='pseudo'
            size='huge'
            type='text'
            required
            fluid 
            onChange={handleChange}
            name='name'
            value={username}
          />
        </Form.Field>
        <Form.Field>
          <Input
            className="navbar__login-btns"
            label={{ icon: 'asterisk', color: 'teal'  }}
            labelPosition='left corner'
            icon={<Icon name='mail' color='teal' />}
            iconPosition='left'
            placeholder='e-mail'
            size='huge'
            type='email'
            required
            fluid 
            onChange={handleChange}
            name='email'
            value={email}
          />
        </Form.Field>
        <Form.Field>
        <Input
          className="navbar__login-btns"
          label={{ icon: 'asterisk', color: 'teal'  }}
          labelPosition='left corner'
          icon={<Icon name='lock' color='teal' />}
          iconPosition='left'
          placeholder='mot de passe'
          size='huge'
          type='password'
          required
          fluid 
          onChange={handleChange}
          name='password'
          value={password}
        />
        </Form.Field>
        <Form.Field>
          <Input
            className="navbar__login-btns"
            label={{ icon: 'asterisk', color: 'teal'  }}
            labelPosition='left corner'
            icon={<Icon name='lock' color='teal' />}
            iconPosition='left'
            placeholder='confirmer le mot de passe'
            size='huge'
            type='password'
            required
            fluid 
            onChange={verifPassword}
            id='password_verif'
          />
        </Form.Field>
        {/*  <Form.Field >
            <Input icon='lock' iconPosition='left' placeholder='Confirm Password' type="password" onChange={handleChange} />
          </Form.Field> */}

          <Button size='huge' fluid color='teal' type='submit' disabled={submitstate}>Créer</Button>

          {errorCode === 422 && 
            <Message className="error__mail">
              <Message.Header className="error__mail__content">L'adresse E-mail existe déjà !</Message.Header>
            </Message>
          }
          <>
          { (errorCode === 201) &&
          <>
            <Message positive className="succes__upload">
              <Message.Header className="succes__upload__content" > Compte créé.</Message.Header>
            </Message>
            <Button icon='close' color='teal' size='big' onClick={() => window.location.reload()}> Fermer la fenêtre </Button>
          </>
          }
          </>
        </Form>

      </Modal.Content>
    </Modal>
  </>

  );

};

export default ModalCreate;
