import React, { useState, useEffect } from "react";

import "./styles.scss";

import Background from './background.png';
import BackgroundTriange from './background-triangles.png';
import Sound from './sound.png';

const LandingPage = () => {
    const [offsetY, setOffsetY] = useState(0);
  const handleScroll = () => setOffsetY(window.pageYOffset);

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const renderContent = () => (
    <>
      <div className="Parallax__content__heading">
        <h1 className="Parallax__content__heading__text">Sound'Clock</h1>
        <h2 className="Parallax__content__heading__caption">
         Bienvenue sur Sound'Clock, le site de partage de musiques !
        </h2>
      </div>
      <div className="Parallax__content__third">
        <p>
          <b>1. Inscrivez vous.</b> 
        </p>
        <p>
          <b>2. Remplissez votre profil.</b>
        </p>
        <p>
          <b>3. Partagez vos musiques </b> et cr√©ez vos playlist !
        </p>
      </div>
    </>
  );

  return (
    <section className="Parallax">
      <div
        className="Parallax__background"
        style={{ transform: `translateY(-${offsetY * 0.5}px)`,
            "background-image":
              `url(${Background})`
         }}
      />
      <div
        className="Parallax__background-triangles"
        style={{ transform: `translateY(${offsetY * 0.8}px)`,
        "background-image":
         `url(${BackgroundTriange})` }}
      />
      <div className="Parallax__content">{renderContent()}</div>
    </section>
  );
}


export default LandingPage;
