// IMPORT NPM
import { useSelector } from 'react-redux';
import { NavLink } from 'react-router-dom';
import PropTypes from 'prop-types';

// == IMPORT ASSETS
import Music from '../Cards/Music';
import User from '../Cards/User';
import Playlist from '../Cards/Playlist';
import './styles.scss';

const Columns = ( { 
  baseApiUrl,
  handleOnClickUser
 }) => {

  const musics = useSelector((state) => state.musics.list);

  const users = useSelector((state) => state.users.list)

  const playlists = useSelector((state) => state.playlists.list);

  const onWheel = (e) => {
    e.preventDefault()
    const container = document.getElementById('container')
    const targetDiv = e.target.id;
    const containerScrollPosition = document.getElementById(targetDiv).scrollLeft
    container.scrollTo({
        top: 0,
        left: containerScrollPosition + e.deltaY,
        behaviour: 'smooth' //if you want smooth scrolling
    })
}

return(

  <section className="columns">

    <div className='columns-container columns-container__col-1' id='columns-musics' onWheel={onWheel}>

      <div className='columns-container__title'>
        Top 10 Music
      </div>
      {musics.map((music) => (
        <Music
          key={music.id}
          name={music.title}
          date={music.releaseDate}
          image={music.picture}
          description={music.description}
          song={music.file}
          nbLike={music.nbLike} 
          nbListened={music.nbListened} 
          baseApiUrl={baseApiUrl}   
          />
      ))}
    </div>

    {/* //* Scrollbar hide placeholder  
    <div className="columns-container__placeholder"></div>
    */ }

    <div className='columns-container columns-container--columns columns-container__col-2'>

      <span className="columns-container__title" >Top 10 Artistes</span>
      {users.map((user) => (
      //<NavLink to={`/user/${user.slug}`} >
        <User
          key={user.id}
          certification={user.certification}
          date={user.createdAt}
          description={user.description}
          email={user.email}
          label={user.label}
          name={user.name}
          slug={user.slug}
          image={user.picture}
          baseApiUrl={baseApiUrl}   
          handleOnClickUser = {handleOnClickUser}
          />
         // </NavLink>
      ))}
    </div>

    {/* //* Scrollbar hide placeholder  
    <div className="columns-container__placeholder"></div>
    */ }

    <div className='columns-container columns-container__col-3'>
      <span className="columns-container__title" >Top 10 Playlist</span>
      {playlists.map((playlist) => (
        <Playlist
          key={playlist.id}
          name={playlist.name}
          date={playlist.createdAt}
          image={playlist.picture}
          description={playlist.description}
          nbLike={playlist.nbLike} 
          baseApiUrl={baseApiUrl}   
          />
      ))}
    </div>

    {/* //* Scrollbar hide placeholder  
    <div className="columns-container__placeholder"></div>
    */ }

  </section> 

  );
};

Columns.propTypes = {
  baseApiUrl: PropTypes.string.isRequired,
}

export default Columns;
