// == IMPORT NPM
import axios from 'axios';

// == IMPORT ASSETS
import {
  LOGIN, saveUser,
} from '../actions/user';

// on crée une instance d'axios
// on va pouvoir se configurer cette instance
// avant de s'en servir

const baseURL = 'http://denisjulien-server.eddi.cloud/projet-9-sound-clock-back/public'

const authMiddleWare = (store) => (next) => (action) => {
  switch (action.type) {
    case LOGIN: {
      const { users: { username, password } } = store.getState();

      // Send request with User Login for JWT Token
      axios.post('http://denisjulien-server.eddi.cloud/projet-9-sound-clock-back/public/api/login_check', {
        username,
        password,
      }).then(
        (response) => {
          console.log(response);
          // Save New Token to Localstorage
          localStorage.setItem('itemName', response.data.token)
          // Save User Data to State (Token and Username)
          store.dispatch(saveUser(response.data));
        },
      ).catch(
        () => console.log('error'),
      );
    }
    break;
    default:
    next(action);
