// == IMPORT NPM
import { useSelector } from "react-redux";

// IMPORT ASSETS
import "./styles.scss";
import Music from '../Cards/Music';
import Playlist from '../Cards/Playlist';
import User from '../Cards/User';

const Results = ( {
  baseApiUrl
}) => { 

  // Variable for search Query Term
  const research = useSelector((state) => state.research.research);  

  // Obejct from search Query Term
  const searchList = useSelector((state) => state.research.list);
  // console.log('Object from Resutls/Index component : ', searchList.genre)

  return(

    <div className="results"> 
      <h1 className="results__h1"> Search Result for : <span className="results__h1--term">{research}</span></h1>

      <h2 className="results__h2"> Music</h2>
        <div className="cards__container">
        { searchList.music.slice(0,10).map((music) =>( 
            <Music
              key={music.description}
              name={music.title}
              date={music.releaseDate}
              image={music.picture}
              description={music.description}
              song={music.file}
              nbLike={music.nbLike} 
              nbListened={music.nbListened} 
              baseApiUrl={baseApiUrl}   
              />
          ) ) }
        </div>

      <h2 className="results__h2"> Artiste/Utilisateur</h2>
        <div className="cards__container">
        { 
          searchList.user.slice(0,10).map((user) =>( 
            <User
              key={user.description}
              certification={user.certification}
              date={user.createdAt}
              description={user.description}
              email={user.email}
              label={user.label}
              name={user.name}
              image={user.picture}
              baseApiUrl={baseApiUrl}   
              />
          ) ) }
        </div>

      <h2 className="results__h2"> Playlist</h2>
        <div className="cards__container">
        { 
          searchList.playlist.slice(0,10).map((playlist) =>( 
            <Playlist
              key={playlist.description}
              name={playlist.title}
              date={playlist.createdAt}
              image={playlist.picture}
              description={playlist.description}
              nbLike={playlist.nbLike} 
              baseApiUrl={baseApiUrl}   
              />
          ) ) }
        </div>

      <h2 className="results__h2"> Genre</h2>
        <div className="cards__container">
        { 
          searchList.genre.slice(0,10).map((genre) =>( 
            <Playlist
              key={genre.description}
              date={genre.createdAt}
              description={genre.description}
              name={genre.title}
              image={genre.picture}
              baseApiUrl={baseApiUrl}   
              />
          ) ) }
        </div>
    </div>

  );
};

export default Results;
