// == IMPORT NPM
import { useSelector } from "react-redux";

// IMPORT ASSETS
import "./styles.scss";
import Music from '../Cards/Music';
import Playlist from '../Cards/Playlist';

const Results = ( {
  baseApiUrl
}) => { 

  //TODO Picking up the musics results for the user from the user musics API
  const musics = useSelector((state) => state.musics.list);
  const playlists = useSelector((state) => state.playlists.list);
  const research = useSelector((state)=> state.research.research);  
  return(

    <div className="results"> 
      <h1 className="results__h1"> Search Result for : <span className="results__h1--term">{research}</span></h1>

      <h2 className="results__h2"> Music</h2>
        <div className="cards__container">
          {musics.slice(0,10).map((music) =>( 
            <Music
              key={music.id}
              id={music.id}
              name={music.title}
              date={music.releaseDate}
              image={music.picture}
              description={music.description}
              song={music.file}
              nbLike={music.nbLike} 
              nbListened={music.nbListened} 
              baseApiUrl={baseApiUrl}   
              />
          ) ) }
        </div>

      <h2 className="results__h2"> Playlist</h2>
        <div className="cards__container">
          {playlists.slice(0,10).map((playlist) =>( 
            <Playlist
              key={playlist.id}
              name={playlist.title}
              date={playlist.createdAt}
              image={playlist.picture}
              description={playlist.description}
              nbLike={playlist.nbLike} 
              baseApiUrl={baseApiUrl}   
              />
          ) ) }
        </div>
    </div>

  );
};

export default Results;
