// == IMPORT NPM
import axios from 'axios';

// == IMPORT ASSETS
import {
  LOGIN, 
  GET_USER, 
  getUser, 
  saveUser, 
  GET_USER_WITH_SLUG,
  saveUserWithSlug,
} from '../actions/user';

// on crée une instance d'axios
// on va pouvoir se configurer cette instance
// avant de s'en servir

const baseURL = 'http://denisjulien-server.eddi.cloud/projet-9-sound-clock-back/public'

const authMiddleWare = (store) => (next) => (action) => {
  switch (action.type) {
    case LOGIN: {
      //console.log('etape : 3 je passe par le middleware login je vais au get user');
      const { users: { username, password } } = store.getState();

      // Send request with User Login for JWT Token
      axios.post(`${baseURL}/api/login_check`, {
        username,
        password,
      }).then(
        (response) => {
          localStorage.setItem('itemName', response.data.token)
          const token = response.data.token;
          let base64Url = token.split('.')[1];
          let base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
          let jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
              return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
          }).join(''));

          let payload =  JSON.parse(jsonPayload);
          console.log('je suis le payload', payload);

          store.dispatch(getUser(username));   
        },
      ).catch(
        () => console.log('Login error'),
      );
    }
    break;
    case GET_USER: 
      //console.log('etape : 4, me voila au get user, je passe au saveUser');

      const {users: { username, token, list}} = store.getState();

      let user = list.find(user => user.email === username);
      user.token = token;
      store.dispatch(saveUser(user)); 

    break;
    case GET_USER_WITH_SLUG: 

      console.log('Get User With slug prop passed to middleware ',action.slug);
      console.log('voici la route de l\'api : ', `${baseURL}/api/user/${action.slug}`);
      axios.get(`${baseURL}/api/user/${action.slug}`).then(
        (response) => {
          console.log('je suis le response du get user with slug', response);
          store.dispatch(saveUserWithSlug(response.data));   
        },
      ).catch(
        () => console.log('Get User By Slug Error'),
      );

    break;
  default:
    next(action);
  }
};

export default authMiddleWare;
