// == IMPORT NPM
import React from "react";
import {Header, Modal, Button, Icon, Form, Input } from "semantic-ui-react";
import './styles.scss'

const ModalCreate = ({
  name,
  email,
  password,
  changeField,
  handleCreate,
  //changeModalState,
}) => { 

  const handleChange = (evt) => {
    changeField(evt.target.value, evt.target.name);
  };

  const handleSubmit = (evt) => {
    evt.preventDefault();
    handleCreate();
  }; 

 /*  const handleModalState = (evt) => {
    dispatch(changeModalState(evt.target.name));
  } */

  //const createButton = useSelector((state) => state.users.createButton);
  //onClick={handleModalState}
  const [open, setOpen] = React.useState(false)
  return (
  <>


    <Modal
    centered={false}
    //onClose={{handleModalState}}
    onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      trigger={<Button   name="createButton" color="teal" size="big" >
      <Icon name="add user" />
      Create Account
    </Button>}
      >
      <Header color="teal">Creation de votre compte</Header>
      <Modal.Content>

        <Form className="modal__creat__form" onSubmit={handleSubmit}>
          <Form.Field>
            <Input icon='user' iconPosition='left' placeholder='Pseudo' type="text" onChange={handleChange} value={name} name='name' />
          </Form.Field>
          <Form.Field>
            <Input icon='mail' iconPosition='left' placeholder='E-mail' type="email" onChange={handleChange} value={email} name='email' />
          </Form.Field>
          <Form.Field >
            <Input icon='lock' iconPosition='left' placeholder='Password' type="password" onChange={handleChange} value={password} name='password'  />
          </Form.Field>
         {/*  <Form.Field >
            <Input icon='lock' iconPosition='left' placeholder='Confirm Password' type="password" onChange={handleChange} />
          </Form.Field> */}

          <Button size='large' fluid color='teal'  type='submit'  >Cr√©er</Button>

        </Form>

      </Modal.Content>
    </Modal>
  </>

  );

};

export default ModalCreate;
